import React, { useRef, useEffect, useState } from 'react';

const TextAreaComponent = ({ inputWidth, inputHeight, areaHeight, querytxt, handleAreaChange, handleEnter }) => {
    const [height, setHeight] = useState(inputHeight);
    const textareaRef = useRef(null);

    const adjustHeight = () => {
        if (textareaRef.current) {
            const newHeight = textareaRef.current.scrollHeight;
            setHeight(newHeight);
        }
    };

    useEffect(() => {
        adjustHeight(); // Initial adjustment
    }, [querytxt]); // Adjust whenever the text changes

    return (
        <textarea
            ref={textareaRef}
            style={{
                backgroundColor: "#F8F8F8",
                width: inputWidth,
                height: height,
                fontSize: inputHeight - 14,
                border: '1px solid #D3D3D3',
                position: 'absolute',
                left: '50%',
                top: window.innerHeight / 2 + areaHeight / 2 - height / 2,
                transform: 'translate(-50%, -50%)',
                borderRadius: '10px',
                overflowY: "hidden",
                resize: 'none'
            }}
            placeholder="Message"
            value={querytxt}
            onChange={(e) => {
                handleAreaChange(e);
                adjustHeight(); // Adjust height on change
            }}
            onKeyDown={handleEnter}
        />
    );
};

export default TextAreaComponent;
